import React, { Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardText, CardActions, Button, CardMenu, IconButton } from 'react-mdl';
import avatar from '../Image-2.png';

class Projects extends Component {
  constructor(props) {
    super(props);
    this.state = { activeTab: 0 };
  }

  toggleCategories() {
    if(this.state.activeTab === 0) {
      return (
        <Card shadow={5} style={{minWidth: '450', margin: 'auto'}}>
          <CardTitle style={{color: '#fff', height: '176px', background: 'source={avatar}'}}>This Website</CardTitle>
          <CardText>Used React.js to build this site</CardText>
          <CardActions border>
            <Button colored>Github</Button>
            <Button colored>Live Demo</Button>
          </CardActions>
          <CardMenu style={{color: '#fff'}}>

          </CardMenu>
        </Card>
      );
    } else if(this.state.activeTab === 1) {
      return (
        <div><h1>This is Vue</h1></div>
      );
    } else if(this.state.activeTab === 2) {
      return (
        <div><h1>This is MongoDB</h1></div>
      )
    } else {
      return (
        <div><h1>This is Machine Learning</h1></div>
      )
    }
  }

  render() {
    return(
      <div className="category-tabs">
        <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
          <Tab>React</Tab>
          <Tab>Vue</Tab>
          <Tab>MongoDB</Tab>
          <Tab>Machine Learning</Tab>
        </Tabs>

        <section className="projects-grid">
          <Grid className="projects-grid">
            <cell col={12}>
              <div className="content">
                {this.toggleCategories()}
              </div>
            </cell>
          </Grid>

        </section>

      </div>
    );
  }
}

export default Projects;
